sourceCode.script:
$logDate = Get-Date -UFormat "%Y%m%d"
$logFile = "E:\SailPoint\Scripts\Logs\ConnectorAfterModify_$logDate.log"
$command = "E:\SailPoint\Scripts\AfterModify.ps1"
$enableDebug = $true
#====================-------Helper functions-------====================
function LogToFile([String] $info) {
  $info | Out-File $logFile -Append
}
#====================-------Get the request object-------====================
Try{
	  if($enableDebug) {
		  $timestampString = (Get-Date -Format "yyyy-MM-dd_HH-mm-ss").ToString()
		  LogToFile("[$timestampString] AfterModify: Entering AfterModify rule")
	  }
	  Add-type -path utils.dll;
	  $sReader = New-Object System.IO.StringReader([System.String]$env:Request);
	  $xmlReader = [System.xml.XmlTextReader]([sailpoint.utils.xml.XmlUtil]::getReader($sReader));
	  $requestObject = New-Object Sailpoint.Utils.objects.AccountRequest($xmlReader);
	  $requestAsString = $env:Request
	  $op = $requestObject.Operation
	  
	  $nativeIdentity = $requestObject.nativeIdentity
	  foreach ($attribute in $requestObject.AttributeRequests){
			if($attribute.Name -eq "sAMAccountName") {
				$sAMAccountName = $attribute.value
			}
	  }

	  if($enableDebug) {
		  LogToFile("AfterModify: Request as XML object is: $requestAsString")
	  }
	  
	  if ($op -eq "Modify") {
			 LogToFile("AfterModify - Execute Modify Operation Scripts")
			 $output = Invoke-Expression $command
	  }
	   if ($op -eq "Disable") {
			 LogToFile("AfterModify - Execute Modify Operation Scripts")
			 $output = Invoke-Expression $command
	  }

}Catch{
  $ErrorMessage = $_.Exception.Message
  $ErrorItem = $_.Exception.ItemName
  if($enableDebug) {
	  LogToFile("AfterModify: Error: Item = $ErrorItem -> Message = $ErrorMessage")
  }
}
if($enableDebug) {
  $timestampString = (Get-Date -Format "yyyy-MM-dd_HH-mm-ss").ToString()
  LogToFile("[$timestampString] AfterModify: Exiting After Modify rule")
}
