import java.util.UUID; 

String ruleName="Rule - WebServiceBeforeOperationRule - Account Aggregation Input";
log.info("Entering : "+ruleName);
UUID uuid = UUID.randomUUID();   //randomly generates a UUID
String uuidAsString = uuid.toString();  
log.info("Randomly Generated UUID: "+uuidAsString);
Map bodyMap = new HashMap();
JSONObject jsonBody = new JSONObject();
log.info("Initial RequestBody : "+requestEndPoint.getBody());    
jsonBody.put("sourcename", "ohidev3");
jsonBody.put("uuid", uuidAsString);   
bodyMap.put("jsonBody", jsonBody.toString()); 
requestEndPoint.setBody(bodyMap); 
log.info("Final RequestBody : "+requestEndPoint.getBody());
