import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.object.ProvisioningPlan.AccountRequest.Operation;
import sailpoint.object.ProvisioningPlan.AttributeRequest;
import sailpoint.object.ProvisioningPlan;
import java.util.List;
import java.util.Map;
import org.json.JSONObject;
import sailpoint.object.ProvisioningPlan.Operation;
import sailpoint.object.Attributes;

String ruleName="Rule - WebServiceBeforeOperationRule - Microstrategy CreateOperation Rule";
log.info("Entering : "+ruleName);

if(provisioningPlan!=null)
{
   AttributeRequest atrq=null;
   String nativeIdentity="";
   String displayName="";
   String active=""; 
      
   List accountRequests=provisioningPlan.getAccountRequests();
   Map bodyMap = new HashMap();
   JSONObject jsonBody = new JSONObject();
   Attributes attrs = new Attributes();
   List role=new ArrayList();
   log.info("Initial RequestBody : "+requestEndPoint.getBody());
   for(ProvisioningPlan.AccountRequest accReq:accountRequests)
   {
     log.info("total accountRequests : "+accountRequests.size());
	 nativeIdentity=accReq.getNativeIdentity();
	 atrq=accReq.getAttributeRequest("displayName");
	 displayName=atrq.getValue();
	 atrq=accReq.getAttributeRequest("active");
	 active=atrq.getValue().toString();
	 atrq=accReq.getAttributeRequest("role");
	 if(atrq.getName().equals("role"))
     {
	   	log.info("atrq.getValue().getClass() : "+atrq.getValue().getClass());
		if(atrq.getValue() instanceof String)
		{
			role.add(atrq.getValue());
        }
        else if(atrq.getValue() instanceof List)
        {
            role=atrq.getValue();
        }				  		
     }     
   }
   log.info("nativeIdentity : "+nativeIdentity);
   jsonBody.put("loginName", nativeIdentity);
   jsonBody.put("displayName", displayName);
   jsonBody.put("active", active);
   jsonBody.put("userRoleList", role);
   log.info("jsonBody : "+jsonBody.toString());   
   bodyMap.put("jsonBody", jsonBody.toString());
   requestEndPoint.setBody(bodyMap);
   log.info("Final RequestBody : "+requestEndPoint.getBody());
}
log.info("Exiting rule : "+ruleName);
return requestEndPoint;
