import sailpoint.tools.GeneralException;
import sailpoint.api.*;
import sailpoint.object.*;
import java.util.ArrayList;
import java.util.Iterator;
import java.util.List;
import java.util.logging;
import java.util.Random;
import java.util.regex.Pattern;
import java.lang.*;
import java.text.Normalizer;
import java.text.Normalizer.Form;
import org.apache.commons.lang.StringUtils;

int maxIteration = 1;
String preferredFirstName = null;
String preferredLastName = null;
String firstName = null;
String lastName = null;
String middleName = null;

public String replaceConsecutiveSpecialCharacters(String emailGenerated)
{
  for (int i=0; i < emailGenerated.length(); i++) 
  {
      String repchar = "";
      int count = 1;
      if(((emailGenerated.charAt(i) == '_') || (emailGenerated.charAt(i) == '.')))
      {
        int j =i;
        while (j+1 < emailGenerated.length() && emailGenerated.charAt(j)== emailGenerated.charAt(j+1))
        {
          j++;
          count++;
        }
        if((emailGenerated.charAt(i) == '_'))
        {
          int k = 0;
          while(k < count)
          {
            repchar = repchar+"_";
            ++k;
          }
          emailGenerated = emailGenerated.replaceAll(repchar,"_");
        }
        if((emailGenerated.charAt(i) == '.'))
        {
          int k = 0;
          while(k <count)
          {
            repchar = repchar+".";
            ++k;
          }
          emailGenerated = emailGenerated.replace(repchar,".");
        }
      }
   }
  return emailGenerated;
}

public String removeBadCharacters(String inputString) {
	  return inputString.replaceAll("[^A-Za-z0-9- ]", "").replaceAll(" ","_").replaceAll("'","").trim();
}
public String removeDiacriticalMarks(String inputString) {
		inputString = StringUtils.trimToEmpty(inputString);
		inputString = Normalizer.normalize(inputString, Normalizer.Form.NFD); 
		inputString = Pattern.compile("\\p{InCombiningDiacriticalMarks}+").matcher(inputString).replaceAll("");
		return inputString;
}
public String generateMail (String firstName,  String lastName, String middleName, int iteration) {

  // Data protection.
  firstName  = StringUtils.trimToNull(firstName);
  lastName   = StringUtils.trimToNull(lastName);
  middleName = StringUtils.trimToNull(middleName);

  if ( ( firstName == null ) || ( lastName == null ) )
  return null;

  firstName = removeBadCharacters(removeDiacriticalMarks(firstName));
  middleName= removeBadCharacters(removeDiacriticalMarks(middleName));
  lastName  = removeBadCharacters(removeDiacriticalMarks(lastName));

  // Set Initial Default Values
  String mail = "@harvardpilgrim.org";

  // Below logic is added to change mail extension for specific cost center codes
  String costCenterCode = identity.getAttribute("costCenterCode");
  if( (costCenterCode!=null) && (costCenterCode.equals("355") || costCenterCode.equals("324") || costCenterCode.equals("358")) ) 
  {
    mail = "@carepartnersct.com";  

  }
  if( (costCenterCode!=null) && (costCenterCode.equals("998")  || costCenterCode.equals("999")) )  
  {
    mail = "@hphci.harvard.edu";

  }
  String middleInitial     = "";
  String mailPrefix        = "TEST_";
  String sourceName        = "HPHC-ActiveDirectory [source]";

  // Build Distinguished Name based on Iteration and if middleName is not null
  if (iteration == 0)
    mail = mailPrefix + firstName +"_"+ lastName + mail;
  else if (iteration == 1 && !StringUtils.isBlank(middleName)){
    middleInitial     = middleName.substring(0,1);
    mail = mailPrefix + firstName + "_" + middleInitial + "_" + lastName + mail;
    }
  else{
    mail = "NO_EMAIL";
  }
  log.error("AccountGenerator Rule Operation - Mail Iteration Value: " + mail);
  // Evaluate Uniqueness and Loop through the method again if not unique.
  if (isUnique (mail, sourceName))
    return mail;
  else if (iteration <= maxIteration)
    return generateMail (firstName,  lastName, middleName, (iteration + 1));
  else
    return "NO_EMAIL";  //Return static email
}


public boolean isUnique (String mail, String sourceName) throws GeneralException {
  return (idn.isUniqueLDAPValue(identity.getName(),sourceName, "mail", mail));
}

preferredFirstName = identity.getAttribute("preferredFirstName");
preferredLastName = identity.getAttribute("preferredLastName");
if(preferredFirstName!=null && preferredLastName!=null)
{
  firstName = preferredFirstName;
  lastName = preferredLastName;
  middleName = identity.getAttribute("middleName");
}
else
{
  firstName = identity.getFirstname();
  lastName = identity.getLastname();
  middleName = identity.getAttribute("middleName");
}

String emailGenerated = generateMail(firstName,lastName,middleName,0);
if(emailGenerated!=null)
{
  log.info("Intial Email Generated : "+emailGenerated);
  emailGenerated = replaceConsecutiveSpecialCharacters(emailGenerated);
  log.info("Email after Removal of consecutive special characters : "+emailGenerated);
}
return emailGenerated;
