import sailpoint.tools.GeneralException;
import java.util.Iterator;
import sailpoint.object.*;
import java.util.ArrayList;
import sailpoint.api.*;
import sailpoint.object.*;
import java.util.Iterator;
import java.util.List;
import org.apache.commons.lang.StringUtils;
import java.util.Random;

int uidNumber=0;
boolean isUniqueUidNumber = false;
int maxIteration = 1;
String ruleName =  "THP ActiveDirectory uidNumber";
log.info("Entering rule : "+ruleName);
while((isUniqueUidNumber!=true) && (maxIteration<=100))
{
  int rangeMax = 90000000;
  Random rand = new Random();
  uidNumber = rand.nextInt(rangeMax)+10000000;
  String sourceId = application.getId();
  //idn.attrSearchCountAccounts expects a List as a input
  List sourceIdList = new ArrayList();
  sourceIdList.add(sourceId);
  List uidNumberList = new ArrayList();
  uidNumberList.add(Integer.toString(uidNumber));
  if(idn.attrSearchCountAccounts(sourceIdList, "uidNumber","Equals",uidNumberList)==0)
  {
    isUniqueUidNumber = true;
  }
  ++maxIteration;
}
log.info("uidNumber : "+uidNumber);
log.info("Exiting rule : "+ruleName);
return uidNumber;
