	import java.util.ArrayList;
	import java.util.Date;
	import java.text.SimpleDateFormat;

	import sailpoint.object.Identity;
	import sailpoint.object.Application;
	import sailpoint.object.Link;
	import sailpoint.object.ProvisioningPlan;
	import sailpoint.object.ProvisioningPlan.Operation;
	import sailpoint.object.ProvisioningPlan.AccountRequest;
	import sailpoint.object.ProvisioningPlan.AccountRequest.Operation;
	import sailpoint.object.ProvisioningPlan.AttributeRequest;
	import sailpoint.api.IdentityService;
	import sailpoint.tools.Util;

	String Environment="Sandbox";
	String activeOU = null;
	String disabledOU = null;

	if(Environment.equals("Sandbox")) {
     activeOU = "OU=TEST_THPUSERS,DC=thp,DC=tahphq,DC=tahp"; //Sandbox users Active OU
     disabledOU = "OU=TEST_DISABLED_ACCOUNTS,OU=TEST_THPUSERS,DC=thp,DC=tahphq,DC=tahp"; //Sandbox users Disable OU
	 }


	if (Environment.equals("Prod")) {

      activeOU = "OU=THPUSERS,DC=thp,DC=tahphq,DC=tahp"; // Active OU
     disabledOU = "OU=DISABLED_ACCOUNTS,OU=THPUSERS,DC=thp,DC=tahphq,DC=tahp"; // Disable OU
	}

	String adDomainUsers = "CN=TEST_Domain Users,OU=TEST_ACCESS-GROUPS,OU=TEST_THPGROUPS,DC=thp,DC=tahphq,DC=tahp";
    Identity identity = null;
    String nativeIdentity = null;

	if( plan != null ) {
		identity = plan.getIdentity();
		nativeIdentity = plan.getNativeIdentity();
	}

	if(identity != null) {
		log.info("identity: "+ identity.getName() +" :: [AD_PROV] Executing Before Provisioning Rule.... nativeIdentity - " + nativeIdentity );


		log.info("identity: "+ identity.getName() +" :: [AD_PROV] Lifecycle State: " + identity.getAttribute("cloudLifecycleState") );
		if (Util.nullSafeEq(identity.getAttribute("cloudLifecycleState"), "deleteaccount") && !nativeIdentity.contains("LEGAL_HOLD"))  {
			List emptyAttributes = new ArrayList();

			List accountRequests = plan.getAccountRequests();
			for (AccountRequest accountRequest : accountRequests) {
				AccountRequest.Operation op = accountRequest.getOperation();
				String accountName = accountRequest.getNativeIdentity();
      				//delete account after 180 days of term
      				if  ( Util.nullSafeEq(accountRequest.getOperation(), AccountRequest.Operation.Enable)) {
        			  log.info("identity: "+ identity.getName() +" :: [AD_PROV] deleting account after 180 days of term ");
                      if (!accountName.contains("LEGAL_HOLD")) {
					    accountRequest.setOperation(AccountRequest.Operation.Delete);
					  } else {
					    accountRequest.setOperation(AccountRequest.Operation.Disable);
					  }
					    accountRequest.setAttributeRequests(emptyAttributes);
				    }
			}

		} else if (Util.nullSafeEq(identity.getAttribute("cloudLifecycleState"), "removeaccess"))  {
			List emptyAttributes = new ArrayList();

			List accountRequests = plan.getAccountRequests();
			for (AccountRequest accountRequest : accountRequests) {
				AccountRequest.Operation op = accountRequest.getOperation();
      				//Remove all AD Access after 14 days
      				if  ( ( Util.nullSafeEq(accountRequest.getOperation(), AccountRequest.Operation.Enable)) || ( Util.nullSafeEq(accountRequest.getOperation(), AccountRequest.Operation.Modify)) ) {
        				log.info("identity: "+ identity.getName() +" :: [AD_PROV] remove account access after initial term ");


                    List groupList = new ArrayList();
		            accountRequest.add(new AttributeRequest("memberOf", ProvisioningPlan.Operation.Set, groupList));
					accountRequest.setOperation(AccountRequest.Operation.Disable);
					accountRequest.add(new AttributeRequest("AC_NewParent",ProvisioningPlan.Operation.Set, disabledOU) );
				}
			}

		} else if (Util.nullSafeEq(identity.getAttribute("cloudLifecycleState"), "inactive")) {
			List accountRequests = plan.getAccountRequests();
			for (AccountRequest accountRequest : accountRequests) {

				//move account to disabled OU on term when LCS is inactive
				if (Util.nullSafeEq(accountRequest.getOperation(),AccountRequest.Operation.Disable)) {
					log.info("identity: "+ identity.getName() +" :: [AD_PROV] move account to disabled OU on term ");

					accountRequest.add(new AttributeRequest("AC_NewParent",ProvisioningPlan.Operation.Set, disabledOU) );

				}
			}
		} else if( Util.nullSafeEq(identity.getAttribute("cloudLifecycleState"), "active") || Util.nullSafeEq(identity.getAttribute("cloudLifecycleState"), "prehire") ){
			List accountRequests = plan.getAccountRequests();
			for (AccountRequest accountRequest : accountRequests) {

				//move account to active OU on rehire
				if (Util.nullSafeEq(accountRequest.getOperation(),AccountRequest.Operation.Enable)) {
					accountRequest.add(new AttributeRequest("AC_NewParent",ProvisioningPlan.Operation.Set, activeOU) );
					accountRequest.add(new AttributeRequest("msExchHideFromAddressLists", ProvisioningPlan.Operation.Set, false));
				}
			}
		}
	}
		log.info("identity: "+ identity.getName() +" :: [AD_PROV] Complete Before Provisioning Rule....");
